import React from 'react'
import "./style.css"

const Preview = ({files}) => {
    const fileChooser = () => {
        const fileTypeArray = files.fileName.split(".")
        const fileType = fileTypeArray[fileTypeArray.length - 1]
        console.log(fileType)
    }

    return (
        <div className='docs-content'>
            {files.map(file => 

            <img id={file.fileName} style={{maxWidth: "200px"}} src={`//localhost:8000/${file.filename}`} alt={file.originalname}/>

            )}
            <p>{fileChooser}</p>
            </div>

        )
  }

export default Preview
