import React, {useEffect, useState} from 'react'
import "./style.css"

const Preview = ({files}) => {


    const [stateFileName, setStateFileName] = useState("")


    const fileChooser = () => {
        setStateFileName(files.fileName)
        // const fileTypeArray = files.fileName.split(".")
        // const fileType = fileTypeArray[fileTypeArray.length - 1]
    }

    useEffect(()=>{

        console.log(stateFileName)
    }, fileChooser)


    return (
        <div className='docs-content'>
            {files.map(file => 

            <img onClick={fileChooser} key={`${file.fileName}`} style={{maxWidth: "200px"}} src={`//localhost:8000/${file.filename}`} alt={file.originalname}/>

            )}
            </div>

        )
  }

export default Preview
