import React from 'react'
import "./style.css"

const Preview = ({files}) => {

    return (
        <div className='docs-content'>{files.map(file => <img style={{maxWidth: "200px"}} src={`//localhost:8000/${file.filename}`} alt={file.originalname}/>)}</div>

        )
  }

export default Preview
